package koreait.day02;

public class C06_DoubleVar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//변수선언
		float p1;
		double p2;
		//변수선언과 초기화
		float q1 = 3.14f;	//3.14는 double 형식의 리터럴. 8바이트를 4바이트에 대입
							// 		ㄴ float 리터럴은 f로 표시합니다
//		float q1=3.14;		//오류
		double q2 = 3.14;
		System.out.println();
	}

}
